mapper.select:
select 

from pay_retention
where createtime = #{createtime,jdbcType=TIMESTAMP}
  and gameid = #{gameid,jdbcType=BIGINT}
  and gameserverid = #{gameserverid,jdbcType=BIGINT}
  and platformid = #{platformid,jdbcType=BIGINT}

mapper.delete:
delete from pay_retention
where createtime = #{createtime,jdbcType=TIMESTAMP}
  and gameid = #{gameid,jdbcType=BIGINT}
  and gameserverid = #{gameserverid,jdbcType=BIGINT}
  and platformid = #{platformid,jdbcType=BIGINT}
