select

from user_info 

        status = 0

		AND user_key = #{userKey,jdbcType=VARCHAR}


		AND password = #{password,jdbcType=VARCHAR}

	select

    from user_info
	  where user_info.id > #{id,jdbcType=BIGINT}

		limit #{limit};

select

 from user_info


		user_key = #{userKey,jdbcType=VARCHAR}


		AND groupName = #{groupName,jdbcType=VARCHAR}


		AND team_id = #{teamId,jdbcType=BIGINT}


		AND role = #{role,jdbcType=VARCHAR}


		AND status = #{status,jdbcType=VARCHAR}


		AND create_time >= #{startDate,jdbcType=TIMESTAMP}


		AND create_time <= #{endDate,jdbcType=TIMESTAMP}


order by create_time desc

limit #{offset},#{limit};

select
count(1)
from user_info


		user_key = #{userKey,jdbcType=VARCHAR}


		AND groupName = #{groupName,jdbcType=VARCHAR}


		AND team_id = #{teamId,jdbcType=BIGINT}


		AND role = #{role,jdbcType=VARCHAR}


		AND status = #{status,jdbcType=VARCHAR}


		AND create_time >= #{startDate,jdbcType=TIMESTAMP}


		AND create_time <= #{endDate,jdbcType=TIMESTAMP}

select id, user_key, user_name from user_info
AND team_id = #{teamId,jdbcType=BIGINT}
order by user_name
